import React from 'react';

export default function FixedWidth({ width, children, ...rest }) {
    return (
        <span
            style={{
                ...rest,
                width,
                display: 'inline-block',
                textOverflow: 'ellipsis',
                overflow: 'hidden',
                whiteSpace: 'nowrap',
            }}
        >
            {children}
        </span>
    );
}
