import React from 'react';

const methodStyles = method => ({
    width: 60,
    textTransform: 'uppercase',
    display: 'inline-block',
    color: {
        get: 'green',
        delete: 'red',
        post: 'orange',
        put: 'pink',
        patch: 'magenta',
    }[method.toLowerCase()],
});

export default ({ method }) => <span style={methodStyles(method)}>{method}</span>;
