import React from 'react';
import ErrorBoundary from './ErrorBoundary';

const rowStyle = {};

const labelStyle = {
    fontWeight: 'bold',
    padding: 5,
    verticalAlign: 'top',
};

function Row({ children, style, ...rest }) {
    return (
        <tr style={{ ...rowStyle, ...style }} {...rest}>
            {children}
        </tr>
    );
}

function Label({ children, style, ...rest }) {
    return (
        <th style={{ ...labelStyle, ...style }} {...rest}>
            {children}
        </th>
    );
}

function Value({ children, style, ...rest }) {
    return (
        <td style={style} {...rest}>
            <ErrorBoundary>{children}</ErrorBoundary>
        </td>
    );
}

function RowView(props) {
    return (
        <table>
            <tbody>{props.children}</tbody>
        </table>
    );
}

RowView.Row = Row;
RowView.Label = Label;
RowView.Value = Value;

export default RowView;
