// @flow
import * as React from 'react';
import { Divider } from 'antd';

type Props = {
    /** Set to true if list is vertical rather than horizontal **/
    vertical?: boolean,
    /** Separator to render between each item */
    separator?: React.Element<*>,
    /** Children should be items to render */
    children: React.Node,
    wrapperComponent?: React.ComponentType<*>,
};

/**
 * Render list of items with a separator
 */
export default function DelimitedList({
    vertical = false,
    separator = <Divider type={vertical ? 'horizontal' : 'vertical'} />,
    children,
    wrapperComponent,
    ...rest
}: Props) {
    const el = React.Children.map(children, (child, index) => (
        <React.Fragment key={index}>
            {index > 0 && separator}
            {child}
        </React.Fragment>
    ));
    if (wrapperComponent) {
        return React.createElement(wrapperComponent, rest, el);
    }
    return el;
}
