import React from 'react';

import './TextPlaceholder.less';

export default class TextPlaceholder extends React.PureComponent {
    constructor(props) {
        super(props);
        this.width = 50 + Math.random() * 40;
    }

    render() {
        return (
            <div className="djrad--text-placeholder" style={{ width: `${this.width}%` }}>
                &nbsp;
            </div>
        );
    }
}
