// @flow
import * as React from 'react';
import PropTypes from 'prop-types';
import './ButtonBar.less';

type Props = {
    /** Button to render on the left side. Can be a single button or array of buttons */
    leftButtons?: React.Node,
    /** Button to render on the right side. Can be a single button or array of buttons */
    rightButtons?: React.Node,
};

const propTypes = {
    leftButtons: PropTypes.node,
    rightButtons: PropTypes.node,
};

export default function ButtonBar({ leftButtons, rightButtons }: Props) {
    return (
        <div className="djrad--button-bar">
            <div className="djrad--button-bar--left">
                {React.Children.map(leftButtons, button => button)}
            </div>
            <div className="djrad--button-bar--right">
                {React.Children.map(rightButtons, button => button)}
            </div>
        </div>
    );
}

ButtonBar.propTypes = propTypes;
