// @flow
import * as React from 'react';
import cx from 'classnames';

import './FormFieldSet.less';

type Props = {
    className?: string,
    /** Legend for field set*/
    legend: React.Node,
    /**
     * @deprecated Use `legend`
     */
    header?: React.Node,
    /**
     * Fieldset body
     */
    children: React.Node,
};

/**
 * Render field set with some default styles. Applies global class `djrad--form-fieldset--bordered`
 *
 * @deprecated As this is literally a HTML `fieldset` with a class name prefer to just implement this in your project
 * instead
 */
export default function FormFieldSet({
    legend,
    header = legend,
    children,
    className,
    ...rest
}: Props) {
    return (
        <fieldset className={cx('djrad--form-fieldset--bordered', className)} {...rest}>
            <legend>{header}</legend>
            {children}
        </fieldset>
    );
}
