// @flow
import * as React from 'react';
import moment from 'moment';
import DateWidget from './DateWidget';
import type { WidgetProps } from '../FormField';

type Props = WidgetProps & {
    showTime?: boolean | {},
    value?: moment | string,
};

/**
 * Wrapper around `DateWidget` - accepts same props but always sets `showTime`.
 * @see [DateWidget](/Components/DateWidget)
 * @acceptsProps DateWidget
 */
const DateTimeWidget = React.forwardRef((props: Props, ref) => {
    const { showTime = true } = props;
    return <DateWidget {...props} showTime={showTime} ref={ref} />;
});

export default DateTimeWidget;
