// @flow
import React from 'react';
import { Input } from 'antd';

import type { WidgetProps } from '../FormField';

type Props = WidgetProps & {
    value?: any,
};

// Default inputs make no use of meta (which comes from redux-form) and
// react will warn about invalid props so exclude it here for default Input

/**
 * Simple text entry widget. This is the default if no widget is specified on a form field.
 */
export default function InputWidget({ meta, djradFieldName, ...rest }: Props) {
    return <Input {...rest} />;
}
