// @flow
import * as React from 'react';
import { Switch } from 'antd';
import type { WidgetProps } from '../FormField';

type Props = WidgetProps & {
    value?: boolean,
};

/**
 * See [Switch](https://ant.design/components/switch/) for available props
 */
export default function SwitchWidget({ value, onBlur, djradFieldName, ...rest }: Props) {
    // Note that we don't pass through onBlur as for some reason it causes
    // ant to not change to false (I suspect it's actually redux form interpreting
    // the onBlur value incorrectly)
    return <Switch checked={!!value} {...rest} />;
}
