import React from 'react';
import { Input } from 'antd';

/**
 * See [Input.TextArea](https://ant.design/components/input/#Input.TextArea) for available props
 */
export default function TextAreaWidget({ meta, djradFieldName, ...rest }) {
    // Default inputs make no use of meta (which comes from redux-form) and
    // react will warn about invalid props so exclude it here
    return <Input.TextArea {...rest} />;
}
