// @flow
import * as React from 'react';
import type { FormatterProps } from './types';

type Props = FormatterProps & {
    value?: string,
    children?: React.Node,
};

export default function FileFormatter({
    value,
    record,
    fieldName,
    children = value,
    ...rest
}: Props) {
    if (!value) {
        return null;
    }
    return (
        <a href={value} {...rest}>
            {children}
        </a>
    );
}
