// @flow
import * as React from 'react';
import type { FormatterProps } from './types';

type Props = FormatterProps & {
    value?: string,
};

export default function ImageFormatter({ value, record, fieldName, ...rest }: Props) {
    if (!value) {
        return null;
    }
    return <img src={value} {...rest} />; // eslint-disable-line
}
