// @flow
import * as React from 'react';
import { Helmet } from 'react-helmet';

type Props = {
    /* Optionally specify content to render on page */
    children?: React.Node,
    /* Set <title> in <head> and, in default component, used in page <h3>) */
    title?: string,
};

/**
 * Display message for 404. You can override the default component used by registering
 * a custom component for the id 'rad.error.PageNotFound'.
 */
export default function PageNotFound({ children, title = 'Page Not Found' }: Props) {
    if (!children) {
        children = (
            <span>
                <h3>{title}</h3>
                <p>Sorry, that page could not be found.</p>
            </span>
        );
    }
    return (
        <div>
            <Helmet>
                <title>{title}</title>
            </Helmet>
            {children}
        </div>
    );
}
