// @flow
import * as React from 'react';
import { Helmet } from 'react-helmet';

type Props = {
    /* Optionally specify content to render on page */
    children?: React.Node,
    /* Set <title> in <head> and, in default component, used in page <h3>) */
    title?: string,
};

export default function PageServerError({ children, title = 'Unexpected Error' }: Props) {
    if (!children) {
        children = (
            <span>
                <h3>{title}</h3>
                <p>
                    Sorry, there was an unexpected error loading this page. Please try again or
                    contact us for support.
                </p>
            </span>
        );
    }
    return (
        <div>
            <Helmet>
                <title>{title}</title>
            </Helmet>
            {children}
        </div>
    );
}
