// @flow
import { useContext } from 'react';
import { DjradContext } from '../site/components/context';
import Site from '../site/Site';

/**
 * Get the djrad Site instance for this app
 *
 * Prefer this to using the `injectSite` HOC.
 */
export default function useSite(): Site {
    const { djradSite } = useContext(DjradContext);
    return djradSite;
}
