// @flow
import * as React from 'react';
import { Button } from 'antd';
import FileFormatter from '../../components/formatter/FileFormatter';
import type { FormatterProps } from '../../components/formatter/types';

import Field from './Field';
import type { WidgetProps } from '../../components/form/FormField';
import FileUploadWidget from '../../components/form/widgets/FileUploadWidget';

export default class FileField extends Field {
    getDefaultWidget(props: WidgetProps) {
        return (
            <FileUploadWidget {...props}>
                <Button>Click to upload</Button>
            </FileUploadWidget>
        );
    }

    getDefaultFormatter(props: FormatterProps) {
        return <FileFormatter {...props} />;
    }
}
