import React from 'react';
import RangeField from './RangeField';
import InputRangeWidget from '../../components/form/widgets/InputRangeWidget';
import NumberWidget from '../../components/form/widgets/NumberWidget';

export default class IntegerRangeField extends RangeField {
    getDefaultWidget(widgetProps) {
        return (
            <InputRangeWidget
                {...widgetProps}
                inputWidget={NumberWidget}
                inputWidgetProps={{
                    step: 1,
                    precision: 0,
                }}
            />
        );
    }
}
