// @flow
import Relation from './Relation';
import type { Model } from '../Model';

export default class ManyToManyRelation extends Relation {
    throughModel: ?Class<Model>;

    constructor(
        fromModel: Class<Model>,
        model: Class<Model>,
        filterable: boolean,
        throughModel: ?Class<Model> = null
    ) {
        super(fromModel, model, filterable);
        this.throughModel = throughModel;
    }

    isToMany(): boolean {
        return true;
    }
}
