// @flow
import type { Model } from '../Model';

export default class Relation {
    fromModel: Class<Model>;
    model: Class<Model>;
    filterable: boolean;

    constructor(fromModel: Class<Model>, model: Class<Model>, filterable: boolean) {
        this.fromModel = fromModel;
        this.model = model;
        this.filterable = filterable;
    }

    isReverse(): boolean {
        return false;
    }
    isToMany(): boolean {
        return false;
    }
    get supportsSearch(): boolean {
        // eslint-disable-next-line
        console.warn(
            "The property 'supportsSearch' has been replaced with 'filterable', please update usage"
        );
        return this.filterable;
    }
}
