// @flow
import Relation from './Relation';
import type { Model } from '../Model';

export default class ReverseOneToOneRelation extends Relation {
    remoteFieldName: string;

    constructor(
        fromModel: Class<Model>,
        model: Class<Model>,
        remoteFieldName: string,
        filterable: boolean
    ) {
        super(fromModel, model, filterable);
        this.remoteFieldName = remoteFieldName;
    }
    isReverse(): boolean {
        return true;
    }
}
