import oneLine from 'common-tags/lib/oneLine';

export function warn(msg) {
    console.warn(oneLine`${msg}`); // eslint-disable-line
}

const nullLogger = {
    info() {},
    warn() {},
    error() {},
    debug() {},
    log() {},
    table() {},
    group() {},
    groupCollapsed() {},
    groupEnd() {},
};

export const isDev = () => typeof process != 'undefined' && process.env.NODE_ENV === 'development'; // eslint-disable-line

export const devLogger = isDev() ? console : nullLogger;
